// ==UserScript==
// @author         fragger
// @name           IITC plugin: Pan control
// @category       Controls
// @version        0.2.2.20220723.000534
// @description    Show a panning control on the map.
// @id             pan-control
// @namespace      https://github.com/IITC-CE/ingress-intel-total-conversion
// @updateURL      https://iitc.app/build/artifact/PR484/plugins/pan-control.meta.js
// @downloadURL    https://iitc.app/build/artifact/PR484/plugins/pan-control.user.js
// @match          https://intel.ingress.com/*
// @grant          none
// ==/UserScript==

function wrapper(plugin_info) {
// ensure plugin framework is there, even if iitc is not yet loaded
if(typeof window.plugin !== 'function') window.plugin = function() {};

//PLUGIN AUTHORS: writing a plugin outside of the IITC build environment? if so, delete these lines!!
//(leaving them in place might break the 'About IITC' page or break update checks)
plugin_info.buildName = 'test';
plugin_info.dateTimeVersion = '2022-07-23-000534';
plugin_info.pluginId = 'pan-control';
//END PLUGIN AUTHORS NOTE


// use own namespace for plugin
var panControl = {};
window.plugin.panControl = panControl;

panControl.options = {
  //position: 'topleft',
  //panOffset: 350
};

function setup () {
  loadLeafletPancontrol();

  var map = window.map;
  panControl.control = L.control.pan(panControl.options).addTo(map);

  if (!panControl.options.position) { // default: 'topleft'
    // to be above all controls
    $('.leaflet-top.leaflet-left .leaflet-control').first().before(panControl.control.getContainer());
  }

  // L.Control.Pan.css tries to align zoom control with the pan control, but the result sucks
  // so here is our attempt to make it better
  // (adapted from https://github.com/kartena/Leaflet.Pancontrol/pull/20)
  $('<style>').html('\
    .leaflet-left.has-leaflet-pan-control .leaflet-control-zoom,\
    .leaflet-left.has-leaflet-pan-control .leaflet-control-zoomslider { left: unset; }\
    .leaflet-left.has-leaflet-pan-control .leaflet-control-scale { left: -24.5px; }\
    .leaflet-left.has-leaflet-pan-control { left: 24.5px; }\
    .leaflet-left .leaflet-control-pan { left: -24.5px; }\
    .leaflet-control-pan { width: 75px; height: 75px; }\
    .leaflet-touch .leaflet-left.has-leaflet-pan-control { left: 26px; }\
    .leaflet-touch .leaflet-left .leaflet-control-pan { left: -26px; }\
    .leaflet-touch .leaflet-control-pan { width: 86px; height: 114px; }\
    .leaflet-touch .leaflet-left .leaflet-control-pan { margin-left: 10px; }\
  ').appendTo('head');
}
setup.priority = 'low';

function loadLeafletPancontrol () {
  try {
    // https://github.com/kartena/Leaflet.Pancontrol
    // *** included: external/L.Control.Pan.js ***
(function (factory) {
	// Packaging/modules magic dance
	var L;
	if (typeof define === 'function' && define.amd) {
		// AMD
		define(['leaflet'], factory);
	} else if (typeof module !== 'undefined') {
		// Node/CommonJS
		L = require('leaflet');
		module.exports = factory(L);
	} else {
		// Browser globals
		if (typeof window.L === 'undefined')
			throw 'Leaflet must be loaded first';
		factory(window.L);
	}
}(function (L) {
	'use strict';
	L.Control.Pan = L.Control.extend({
		options: {
			position: 'topleft',
			panOffset: 500
		},

		onAdd: function (map) {
			var className = 'leaflet-control-pan',
				container = L.DomUtil.create('div', className),
				off = this.options.panOffset;

			this._panButton('Up'   , className + '-up',
							container, map, new L.Point(    0 , -off));
			this._panButton('Left' , className + '-left',
							container, map, new L.Point( -off ,  0));
			this._panButton('Right', className + '-right',
							container, map, new L.Point(  off ,  0));
			this._panButton('Down' , className + '-down',
							container, map, new L.Point(    0 ,  off));

			// Add pan control class to the control container
			if (this.options.position === 'topleft') {
				var controlContainer = L.DomUtil.get(map._controlCorners.topleft);
			} else if (this.options.position === 'topright') {
				var controlContainer = L.DomUtil.get(map._controlCorners.topright);
			} else if (this.options.position === 'bottomleft') {
				var controlContainer = L.DomUtil.get(map._controlCorners.bottomleft);
			} else {
				var controlContainer = L.DomUtil.get(map._controlCorners.bottomright);
			}
			if(!L.DomUtil.hasClass(controlContainer, 'has-leaflet-pan-control')) {
				L.DomUtil.addClass(controlContainer, 'has-leaflet-pan-control');
			}

			return container;
		},

		onRemove: function (map) {
			// Remove pan control class to the control container
			var controlContainer = L.DomUtil.get(map._controlCorners.topleft);
			if(L.DomUtil.hasClass(controlContainer, 'has-leaflet-pan-control')) {
				L.DomUtil.removeClass(controlContainer, 'has-leaflet-pan-control');
			}
		},

		_panButton: function (title, className, container, map, offset) {
			var wrapper = L.DomUtil.create('div', className + '-wrap', container);
			var link = L.DomUtil.create('a', className, wrapper);
			link.href = '#';
			link.title = title;
			L.DomEvent
				.on(link, 'click', L.DomEvent.stopPropagation)
				.on(link, 'click', L.DomEvent.preventDefault)
				.on(link, 'click', function(){ map.panBy(offset); }, map)
				.on(link, 'dblclick', L.DomEvent.stopPropagation);

			return link;
		}
	});

	L.Map.mergeOptions({
		panControl: false
	});

	L.Map.addInitHook(function () {
		if (this.options.panControl) {
			this.panControl = new L.Control.Pan();
			this.addControl(this.panControl);
		}
	});

	L.control.pan = function (options) {
		return new L.Control.Pan(options);
	};

	return L.Control.Pan;
}));


;
    $('<style>').html('\
/* Make the default zoom control align with the pan control.\
   \
   This is ugly. \
   The parent box (class="leaflet-top leaflet-left") \
   should make all the child boxes be center-aligned instead. \
   Not sure if that is possible though.\
*/\
.leaflet-left.has-leaflet-pan-control .leaflet-control-zoom {\
	position: relative;\
	left: 24px;\
}\
.leaflet-right.has-leaflet-pan-control .leaflet-control-zoom {\
	position: relative;\
	right: 24px;\
}\
\
/* \
   Make the zoomSlider control \
   (https://github.com/mattiasbengtsson/Leaflet.zoomslider) \
   align with the pan control. \
*/\
.leaflet-left.has-leaflet-pan-control .leaflet-control-zoomslider {\
	position: relative;\
	left: 22px;\
}\
.leaflet-right.has-leaflet-pan-control .leaflet-control-zoomslider {\
	position: relative;\
	right: 22px;\
}\
\
.leaflet-control-pan  {\
	   /* \
	     .leaflet-control-pan-right-wrap: right \
	   + .leaflet-control-pan a: width\
	   = 52 + 24 = 76\
	   */\
	width: 76px; \
	   /* \
	     .leaflet-control-pan-down-wrap: top\
	   + .leaflet-control-pan a: height\
	   = 52 + 24 = 76\
	   */\
	height: 76px;\
}\
\
.leaflet-control-pan > div {\
    -moz-border-radius: 4px;\
    -webkit-border-radius: 4px;\
    border-radius: 4px;\
}\
.leaflet-control-pan > div {\
  box-shadow: 0 1px 7px rgba(0,0,0,0.65);\
}\
.leaflet-control-pan a {\
    background-color: #fff;\
}\
.leaflet-control-pan a{\
    background-position: 50% 50%;\
    background-repeat: no-repeat;\
    display: block;\
}\
.leaflet-control-pan a {\
    -moz-border-radius: 4px;\
    -webkit-border-radius: 4px;\
    border-radius: 4px;\
    width: 23px;\
    height: 23px;\
}\
.leaflet-control-pan a:hover {\
    background-color: #f4f4f4;\
}\
\
.leaflet-control-pan-up-wrap {\
	position:absolute;\
	left:26px;\
}\
.leaflet-control-pan-left-wrap {\
	position:absolute;\
	top:26px;\
}\
.leaflet-control-pan-right-wrap {\
	position:absolute;\
	left:52px;	\
	top:26px;\
}\
.leaflet-control-pan-down-wrap {\
	position:absolute;\
	left:26px;\
	top:52px;\
}\
\
.leaflet-control-pan-up {\
	background-image: url(data:image/png;base64,);\
}\
.leaflet-control-pan-left {\
	background-image: url(data:image/png;base64,);\
}\
.leaflet-control-pan-right {\
	background-image: url(data:image/png;base64,);\
}\
.leaflet-control-pan-down {\
	background-image: url(data:image/png;base64,);\
}\
\
/****** Touch Alterations ******/\
.leaflet-touch .leaflet-control-pan div {\
  border: 2px solid rgba(0, 0, 0, 0.2);\
  box-shadow: none;\
\
  border-radius: 4px;\
}\
\
.leaflet-touch .leaflet-control-pan {\
  width: 89px;\
  height: 119px;\
  margin-left: 8px;\
}\
\
.leaflet-touch .leaflet-right .leaflet-control-pan {\
  margin-left: 0;\
  margin-right: 5px;\
}\
\
.leaflet-touch .leaflet-control-pan a {\
  width: 30px;\
  height: 30px;\
\
  border-radius: 4px;\
}\
\
.leaflet-touch .leaflet-control-pan-up-wrap {\
  left:26px;\
}\
\
.leaflet-touch .leaflet-control-pan-left-wrap {\
  top:40px;\
}\
\
.leaflet-touch .leaflet-control-pan-right-wrap {\
  left:52px;\
  top:40px;\
}\
\
.leaflet-touch .leaflet-control-pan-down-wrap {\
  left:26px;\
  top:80px;\
}\
').appendTo('head');

  } catch (e) {
    console.error('L.Control.Pan.js loading failed');
    throw e;
  }
}

setup.info = plugin_info; //add the script info data to the function as a property
if(!window.bootPlugins) window.bootPlugins = [];
window.bootPlugins.push(setup);
// if IITC has already booted, immediately run the 'setup' function
if(window.iitcLoaded && typeof setup === 'function') setup();
} // wrapper end
// inject code into site context
var script = document.createElement('script');
var info = {};
if (typeof GM_info !== 'undefined' && GM_info && GM_info.script) info.script = { version: GM_info.script.version, name: GM_info.script.name, description: GM_info.script.description };
script.appendChild(document.createTextNode('('+ wrapper +')('+JSON.stringify(info)+');'));
(document.body || document.head || document.documentElement).appendChild(script);

